// src/components/Skills/Skills.jsx
import React from "react";
import avatar from "../../assests/prog_avatar.jpg";
import { Link } from "react-scroll";

const IconButton = ({ href, label, children }) => (
  <a
    href={href}
    target={href.startsWith("http") ? "_blank" : undefined}
    rel={href.startsWith("http") ? "noreferrer" : undefined}
    aria-label={label}
    className="inline-flex h-9 w-9 items-center justify-center rounded-full border border-green-500 text-green-400 hover:text-black hover:bg-green-400 transition"
  >
    {children}
    <span className="sr-only">{label}</span>
  </a>
);

/* ---- Pac-Man bits ---- */
const PacmanLoader = () => (
  <div className="mx-auto mt-6 h-8 w-24 relative overflow-visible">
    <div className="absolute inset-0 flex items-center gap-2">
      {Array.from({ length: 6 }).map((_, i) => (
        <span
          key={i}
          className="inline-block h-2 w-2 rounded-full bg-green-400 opacity-70"
          style={{ animation: `dot-move 1.2s linear ${i * 0.12}s infinite` }}
        />
      ))}
    </div>
    <div
      className="absolute left-0 top-1/2 -translate-y-1/2"
      style={{
        width: 18,
        height: 18,
        borderRadius: "50%",
        background: "#FFD400",
        clipPath: "polygon(0% 0%, 100% 50%, 0% 100%)",
        animation: "chomp 0.45s ease-in-out infinite",
      }}
      aria-hidden
    />
  </div>
);

const PacmanDivider = () => (
  <div className="relative my-6">
    <div className="h-px w-full bg-green-800" />
    <div className="absolute -top-2 left-0">
      <div
        className="h-4 w-4 rounded-full"
        style={{
          background: "#FFD400",
          clipPath: "polygon(0% 0%, 100% 50%, 0% 100%)",
          animation: "slide 4s linear infinite",
        }}
        aria-hidden
      />
    </div>
    <div className="absolute inset-0 flex items-center justify-between px-4">
      {Array.from({ length: 14 }).map((_, i) => (
        <span key={i} className="h-1 w-1 rounded-full bg-green-500 opacity-70" />
      ))}
    </div>
  </div>
);

const Skills = () => {
  return (
    <section id="skills" className="bg-black text-green-400 py-16">
      {/* Pac-Man keyframes (drop into this component or move to your globals.css) */}
      <style>{`
        @keyframes chomp {
          0%, 100% { clip-path: polygon(0% 0%, 100% 50%, 0% 100%); }
          50% { clip-path: polygon(0% 25%, 100% 50%, 0% 75%); }
        }
        @keyframes dot-move {
          0% { transform: translateX(80px); opacity: .1; }
          25% { opacity: .6; }
          100% { transform: translateX(0px); opacity: .9; }
        }
        @keyframes slide {
          0% { transform: translateX(0); }
          100% { transform: translateX(100vw); }
        }
      `}</style>

      <div className="max-w-6xl mx-auto px-4">
        {/* Top grid: avatar + about text */}
        <div className="grid grid-cols-1 md:grid-cols-[340px,1fr] gap-10 items-start">
          {/* Avatar + socials */}
          <div className="flex flex-col items-center">
            <img
              src={avatar}
              alt="Yusriyah Rahman"
              className="h-64 w-64 rounded-full object-cover ring-8 ring-green-500 shadow-xl"
            />
            <div className="mt-6 flex items-center gap-4">
              <IconButton href="mailto:rahman4n@uwindsor.ca" label="Email">
                <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" fill="none" viewBox="0 0 24 24" stroke="currentColor" strokeWidth="1.5">
                  <path strokeLinecap="round" strokeLinejoin="round" d="M21.75 6.75v10.5A2.25 2.25 0 0 1 19.5 19.5H4.5A2.25 2.25 0 0 1 2.25 17.25V6.75m19.5 0L12 13.5 2.25 6.75m19.5 0A2.25 2.25 0 0 0 19.5 4.5H4.5A2.25 2.25 0 0 0 2.25 6.75" />
                </svg>
              </IconButton>

              <IconButton href="https://github.com/yusriyahrahman" label="GitHub">
